binding.script:
if(getprop("sim/model/f15/controls/engs/l-eng-master-guard"))
{
if (!getprop("engines/engine[0]/running") and  getprop("controls/engines/engine[0]/cutoff"))
{
setprop("controls/engines/engine[0]/starter",1);
print("Set crank L");
settimer(func { setprop("controls/engines/engine[0]/cutoff",0) }, 0.2);

}
else setprop("controls/engines/engine[0]/cutoff",1-getprop("controls/engines/engine[0]/cutoff"));
}

if(getprop("sim/model/f15/controls/engs/r-eng-master-guard"))
{
if (!getprop("engines/engine[1]/running") and  getprop("controls/engines/engine[1]/cutoff"))
{
setprop("controls/engines/engine[1]/starter",1);
print("Set crank 1");
settimer(func { setprop("controls/engines/engine[1]/cutoff",0) }, 0.2);

}
else setprop("controls/engines/engine[1]/cutoff",1-getprop("controls/engines/engine[1]/cutoff"));
}
